News.getInitialProps = async ({ req }) => {
  let apiKey = '3e6d0f91b9f14b00a5e63f63eb35ee8b',
  page       = 1,
  status     = false,
  pageSize   = 5;
  const res = await fetch(`https://newsapi.org/v2/top-headlines?country=us&apiKey=${apiKey}&page=${page}&pageSize=${pageSize}`)
  .then(function(response){
    if(!response.ok){
      throw Error(response.statusText)
    }
    return response;
  }).then(function(response){
    status=false;
    return response;
  }).catch(function(error){
    status=true;
  });
  if(status){
    console.log('status')
    console.log(status)
    return { dataArticles: [], status: status }
  } else {
    const data = await res.json();
    return { dataArticles: data.articles, status: status }
  }
}

