  let apiKey = '3e6d0f91b9f14b00a5e63f63eb35ee8b',
  page       = 1,
  errorStatus = false,
  pageSize   = 5;
  const res = await fetch(`https://newsapi.org/v2/top-headlines?country=us&apiKey=${apiKey}&page=${page}&pageSize=${pageSize}`)
  .then(function(response){
    if(!response.ok){
      throw Error(response.statusText)
    }
    return response;
  }).then(function(response){
    console.log(response)
    errorStatus=false;
  }).catch(function(error){
    errorStatus=true;
  });
  if(errorStatus){
    return { dataArticles: [], errorStatus: false }
  } else {
    console.log(data)
    const data = await res.json();
    return { dataArticles: data.articles, errorStatus: false }
  }
}

